mapper.select:
select id, api_name, url, method, is_success , create_time from api_log
and api_name like concat('%', #{apiName}, '%') and url like concat('%', #{url}, '%') and method like concat('%', #{method}, '%') and request like concat('%', #{request}, '%') and response like concat('%', #{response}, '%') and is_success = #{isSuccess} and create_time between #{createTime} and #{endCreateTime}
order by create_time desc

SELECT id,api_name,url,method,is_success,create_time FROM api_log a JOIN ( SELECT id bid FROM api_log
and api_name like concat('%', #{apiLog.apiName}, '%') and url like concat('%', #{apiLog.url}, '%') and method like concat('%', #{apiLog.method}, '%') and request like concat('%', #{apiLog.request}, '%') and response like concat('%', #{apiLog.response}, '%') and is_success = #{apiLog.isSuccess} and create_time between #{apiLog.createTime} and #{apiLog.endCreateTime}
ORDER BY id desc LIMIT #{pageNum}, #{pageSize} ) b ON a.id = b.bid
