INSERT INTO `his_config_info` VALUES (0, 187, 'xjs-webmagic.yml', 'xjs', '', '# spring配置\r\nspring: \r\n\r\n  #返回json的全局时间格式\r\n  jackson:\r\n    date-format: yyyy-MM-dd HH:mm:ss\r\n    time-zone: GMT+8\r\n\r\n  redis:\r\n    host: localhost\r\n    port: 6379\r\n    password: \r\n    database: 0\r\n  datasource:\r\n    druid:\r\n      stat-view-servlet:\r\n        enabled: true\r\n        loginUsername: admin\r\n        loginPassword: 123456\r\n    dynamic:\r\n      druid:\r\n        initial-size: 5\r\n        min-idle: 5\r\n        maxActive: 20\r\n        maxWait: 60000\r\n        timeBetweenEvictionRunsMillis: 60000\r\n        minEvictableIdleTimeMillis: 300000\r\n        validationQuery: SELECT 1 FROM DUAL\r\n        testWhileIdle: true\r\n        testOnBorrow: false\r\n        testOnReturn: false\r\n        poolPreparedStatements: true\r\n        maxPoolPreparedStatementPerConnectionSize: 20\r\n        filters: stat,slf4j\r\n        connectionProperties: druid.stat.mergeSql\\=true;druid.stat.slowSqlMillis\\=5000\r\n      datasource:\r\n          # 主库数据源\r\n          master:\r\n            driver-class-name: com.mysql.cj.jdbc.Driver\r\n            url: jdbc:mysql://localhost:3306/xjs-business?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8\r\n            username: root\r\n            password: root\r\n          # 从库数据源\r\n          slave:\r\n            username: root\r\n            password: 123456\r\n            url: jdbc:mysql://42.192.230.80:3306/xjs-business-slave?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8\r\n            driver-class-name: com.mysql.cj.jdbc.Driver\r\n      # seata: true    # 开启seata代理，开启后默认每个数据源都代理，如果某个不需要代理可单独关闭\r\n\r\n# seata配置\r\nseata:\r\n  # 默认关闭，如需启用spring.datasource.dynami.seata需要同时开启\r\n  enabled: false\r\n  # Seata 应用编号，默认为 ${spring.application.name}\r\n  application-id: ${spring.application.name}\r\n  # Seata 事务组编号，用于 TC 集群名\r\n  tx-service-group: ${spring.application.name}-group\r\n  # 关闭自动代理\r\n  enable-auto-data-source-proxy: false\r\n  # 服务配置项\r\n  service:\r\n    # 虚拟组和分组的映射\r\n    vgroup-mapping:\r\n      ruoyi-system-group: default\r\n  config:\r\n    type: nacos\r\n    nacos:\r\n      serverAddr: 127.0.0.1:8848\r\n      group: SEATA_GROUP\r\n      namespace:\r\n  registry:\r\n    type: nacos\r\n    nacos:\r\n      application: seata-server\r\n      server-addr: 127.0.0.1:8848\r\n      namespace:\r\n\r\n\r\n\r\n# swagger配置\r\nswagger:\r\n  title: 爬虫模块接口文档\r\n  license: Powered By xjs\r\n  licenseUrl: https://www.baidu.com\r\n\r\n\r\n# mybatis配置\r\nmybatis-plus:\r\n    # 配置mapper的扫描，找到所有的mapper.xml映射文件\r\n    mapper-locations: classpath:mapper/*/*.xml\r\n    # typeEnumsPackage: com.xjs.*.enums\r\n    global-config: \r\n      db-config: \r\n        id-type: ASSIGN_ID\r\n    configuration: \r\n      log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\r\n\r\n\r\n\r\n\r\n', '92e58a12b7a5692316c34a0231a434f9', '2022-01-27 09:02:30', '2022-01-27 01:02:31', NULL, '0:0:0:0:0:0:0:1', 'I', 'xjs-666');
