INSERT INTO `config_info` VALUES (93, 'ruoyi-gateway-dev.yml', 'DEFAULT_GROUP', 'spring:\n  redis:\n    host: localhost\n    port: 6379\n    password: \n  cloud:\n    gateway:\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        #尚融宝项目\n        - id: srb-service-core\n          uri: lb://srb-service-core\n          predicates:\n            - Path=/srb_core/**\n          filters:\n            - StripPrefix=1\n\n        #业务爬虫模块\n        - id: xjs-webmagic\n          uri: lb://xjs-webmagic\n          predicates:\n            - Path=/webmagic/**\n          filters:\n            - StripPrefix=1\n\n        #业务统计模块\n        - id: xjs-statistics\n          uri: lb://xjs-statistics\n          predicates:\n            - Path=/statistics/**\n          filters:\n            - StripPrefix=1\n\n        #业务日志模块\n        - id: xjs-log\n          uri: lb://xjs-log\n          predicates:\n            - Path=/log/**\n          filters:\n            - StripPrefix=1\n\n        #第三方api模块\n        - id: xjs-openapi\n          uri: lb://xjs-openapi\n          predicates:\n            - Path=/openapi/**\n          filters:\n            - StripPrefix=1\n\n        #英语模块\n        - id: xjs-english\n          uri: lb://xjs-english\n          predicates:\n            - Path=/english/**\n          filters:\n            - StripPrefix=1\n        \n        #预警模块\n        - id: xjs-warning\n          uri: lb://xjs-warning\n          predicates:\n            - Path=/warning/**\n          filters:\n            - StripPrefix=1\n\n          #业务监控模块\n        - id: xjs-monitor\n          uri: lb://xjs-monitor\n          predicates:\n            - Path=/monitor/**\n          filters:\n            - StripPrefix=1\n\n\n        # 认证中心\n        - id: ruoyi-auth\n          uri: lb://ruoyi-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - CacheRequestFilter\n            - ValidateCode