INSERT INTO `config_info` VALUES (92, 'application-dev.yml', 'DEFAULT_GROUP', 'spring:\n  main:\n    allow-bean-definition-overriding: true\n  autoconfigure:\n    exclude: com.alibaba.druid.spring.boot.autoconfigure.DruidDataSourceAutoConfigure\n    \n  #spring默认缓存配置\n  cache: \n    type: redis\n    redis: \n      #缓存失效时间(毫秒)\n      time-to-live: 3600000\n      #是否缓存空值，防止缓存穿透\n      cache-null-values: true\n    \n\n#jwt配置\njwt: \n  #分钟\n  expireTime: 7200\n  #刷新时长/分钟\n  refreshTime: 12000\n\nredisson: \n  ip: 127.0.0.1\n  port: 6379\n\n# feign 配置\nfeign:\n  sentinel:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 2000\n        readTimeout: 2000\n      # 单独每个client设置超时 (contextId=\'remoteCommonFeign\')\n      remoteCommonFeign: \n        connectTimeout: 30000\n        readTimeout: 30000\n      remoteWebmagic36wallpaperFeign: \n        connectTimeout: 900000\n        readTimeout: 900000\n      remoteWebmagicCopyWritingNetworkFeign:\n        connectTimeout: 120000\n        readTimeout: 120000\n      remoteWebmagicSinaFeign: \n        connectTimeout: 1800000\n        readTimeout: 1800000\n      remoteWebmagicWeiXinSouGouFeign:\n        connectTimeout: 10000\n        readTimeout: 10000\n      remoteTranslationFeign:\n        connectTimeout: 3500\n        readTimeout: 3500\n      gaodeAreaFeignClient: \n        connectTimeout: 10000\n        readTimeout: 10000\n\n\n  compression:\n    request:\n      enabled: true\n    response:\n      enabled: true\n\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n', 'f0bfccb0cd38d71fad589738b49c6644', '2021-12-29 13:39:06', '2022-04-08 08:05:13', 'nacos', '0:0:0:0:0:0:0:1', '', 'xjs-666', '', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (93, 'ruoyi-gateway-dev.yml', 'DEFAULT_GROUP', 'spring:\n  redis:\n    host: localhost\n    port: 6379\n    password: \n  cloud:\n    gateway: \n      \n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n      #商城项目search\n        - id: xjs-mall-search\n          uri: lb://xjs-mall-search\n          predicates:\n            - Path=/mall-search/**\n          filters:\n            - StripPrefix=1\n\n        #商城项目coupon\n        - id: xjs-mall-coupon\n          uri: lb://xjs-mall-coupon\n          predicates:\n            - Path=/mall-coupon/**\n          filters:\n            - StripPrefix=1\n\n        #商城项目member\n        - id: xjs-mall-member\n          uri: lb://xjs-mall-member\n          predicates:\n            - Path=/mall-member/**\n          filters:\n            - StripPrefix=1\n\n        #商城项目order\n        - id: xjs-mall-order\n          uri: lb://xjs-mall-order\n          predicates:\n            - Path=/mall-order/**\n          filters:\n            - StripPrefix=1\n\n        #商城项目ware\n        - id: xjs-mall-ware\n          uri: lb://xjs-mall-ware\n          predicates:\n            - Path=/mall-ware/**\n          filters:\n            - StripPrefix=1\n\n        #商城项目product\n        - id: xjs-mall-product\n          uri: lb://xjs-mall-product\n          predicates:\n            - Path=/mall-product/**\n          filters:\n            - StripPrefix=1\n\n        - id: xjs-mall-product-host\n          uri: lb://xjs-mall-product\n          predicates: \n            - Host=192.168.23.128\n\n\n        #尚融宝项目\n        - id: srb-service-core\n          uri: lb://srb-service-core\n          predicates:\n            - Path=/srb_core/**\n          filters:\n            - StripPrefix=1\n\n        #业务爬虫模块\n        - id: xjs-webmagic\n          uri: lb://xjs-webmagic\n          predicates:\n            - Path=/webmagic/**\n          filters:\n            - StripPrefix=1\n\n 