        - Path=/file/**\n          filters:\n            - StripPrefix=1\n\n# 安全配置\nsecurity:\n  # 验证码\n  captcha:\n    enabled: false\n    type: math\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n  # 不校验白名单\n  ignore:\n    whites:\n      - /auth/logout\n      - /auth/login\n      - /auth/register\n      - /*/v2/api-docs\n      - /csrf\n      - /warning/webSocket/**\n', '42c72440ed8056e81877ac70558381ab', '2021-12-29 13:39:06', '2022-04-07 07:41:30', 'nacos', '0:0:0:0:0:0:0:1', '', 'xjs-666', '', '', '', 'yaml', '');
