-server\n      server-addr: 127.0.0.1:8848\n      namespace:\n\n# mybatis配置\nmybatis:\n    # 搜索指定包别名\n    typeAliasesPackage: com.ruoyi.system\n    # 配置mapper的扫描，找到所有的mapper.xml映射文件\n    mapperLocations: classpath:mapper/**/*.xml\n\n# swagger配置\nswagger:\n  title: 系统模块接口文档\n  license: Powered By ruoyi\n  licenseUrl: https://ruoyi.vip', '7ec59ac102fa57c17272aefcd0b20220', '2021-12-29 13:39:06', '2021-12-29 13:39:06', NULL, '0:0:0:0:0:0:0:1', '', 'xjs-666', NULL, NULL, NULL, 'yaml', NULL);
