# Seata 事务组编号，用于 TC 集群名\r\n  tx-service-group: ${spring.application.name}-group\r\n  # 关闭自动代理\r\n  enable-auto-data-source-proxy: false\r\n  # 服务配置项\r\n  service:\r\n    # 虚拟组和分组的映射\r\n    vgroup-mapping:\r\n      ruoyi-system-group: default\r\n  config:\r\n    type: nacos\r\n    nacos:\r\n      serverAddr: 127.0.0.1:8848\r\n      group: SEATA_GROUP\r\n      namespace:\r\n  registry:\r\n    type: nacos\r\n    nacos:\r\n      application: seata-server\r\n      server-addr: 127.0.0.1:8848\r\n      namespace:\r\n\r\n\r\n\r\n# swagger配置\r\nswagger:\r\n  title: 商城检索接口文档\r\n  license: Powered By xjs\r\n  licenseUrl: https://www.baidu.com\r\n\r\n\r\n# mybatis配置\r\nmybatis-plus:\r\n    # 配置mapper的扫描，找到所有的mapper.xml映射文件\r\n    mapper-locations: classpath:mapper/*/*.xml\r\n    # typeEnumsPackage: com.xjs.*.enums\r\n    global-config: \r\n      db-config: \r\n        id-type: ASSIGN_ID\r\n    configuration: \r\n      log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\r\n\r\n\r\n# Elasticsearch配置\r\nelasticsearch: \r\n  ip: 192.168.23.128\r\n  port: 9200\r\n  scheme: http', '3574ef5c0cf7a22c4207dfd0a5930113', '2022-03-25 21:43:23', '2022-03-25 13:43:24', NULL, '0:0:0:0:0:0:0:1', 'I', 'xjs-666');
