ilters:\n            - StripPrefix=1\n\n        #第三方api模块\n        - id: xjs-openapi\n          uri: lb://xjs-openapi\n          predicates:\n            - Path=/openapi/**\n          filters:\n            - StripPrefix=1\n\n        #英语模块\n        - id: xjs-english\n          uri: lb://xjs-english\n          predicates:\n            - Path=/english/**\n          filters:\n            - StripPrefix=1\n        \n        #预警模块\n        - id: xjs-warning\n          uri: lb://xjs-warning\n          predicates:\n            - Path=/warning/**\n          filters:\n            - StripPrefix=1\n\n          #业务监控模块\n        - id: xjs-monitor\n          uri: lb://xjs-monitor\n          predicates:\n            - Path=/monitor/**\n          filters:\n            - StripPrefix=1\n\n\n        # 认证中心\n        - id: ruoyi-auth\n          uri: lb://ruoyi-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - CacheRequestFilter\n            - ValidateCodeFilter\n            - StripPrefix=1\n        # 代码生成\n        - id: ruoyi-gen\n          uri: lb://ruoyi-gen\n          predicates:\n            - Path=/code/**\n          filters:\n            - StripPrefix=1\n        # 定时任务\n        - id: ruoyi-job\n          uri: lb://ruoyi-job\n          predicates:\n            - Path=/schedule/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: ruoyi-system\n          uri: lb://ruoyi-system\n          predicates:\n            - Path=/system/**\n          filters:\n            - StripPrefix=1\n        # 文件服务\n        - id: ruoyi-file\n          uri: lb://ruoyi-file\n          predicates:\n            - Path=/file/**\n          filters:\n            - StripPrefix=1\n\n# 安全配置\nsecurity:\n  # 验证码\n  captcha:\n    enabled: false\n    type: math\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n  # 不校验白名单\n  ignore:\n    whites:\n      - /auth/logout\n      - /auth/login\n      - /